require 'day2/bathroom_code'

describe BathroomCode do
  context 'with a 9-digit keypad' do
    let(:keypad) do
      [[1,2,3],
       [4,5,6],
       [7,8,9]]
    end

    it 'gets the code' do
      instructions = <<EOM
ULL
RRDDD
LURDL
UUUUD
EOM
      result = described_class.call!(keypad: keypad, instructions: instructions)
      expect(result).to eq '1985'
    end

    it 'solves the first puzzle' do
      instructions = <<EOM





EOM
      result = described_class.call!(keypad: keypad, instructions: instructions)
      expect(result).to eq '84452'
    end
  end

  context 'with a ridiculous keypad' do
    let(:keypad) do
      [[nil, nil,   1, nil, nil],
       [nil,   2,   3,   4, nil],
       [  5,   6,   7,   8,   9],
       [nil, 'A', 'B', 'C', nil],
       [nil, nil, 'D', nil, nil]]
    end

    it 'gets the code' do
      instructions = <<EOM
ULL
RRDDD
LURDL
UUUUD
EOM
      result = described_class.call!(keypad: keypad, instructions: instructions)
      expect(result).to eq '5DB3'
    end

    it 'solves the first puzzle' do
      instructions = <<EOM





EOM
      result = described_class.call!(keypad: keypad, instructions: instructions)
      expect(result).to eq 'D65C3'
    end
  end
end
